/**
 * @author hiroxpepe
 */

// a global object for namespace.
var exmp = {};
exmp.websocket = {};

///////////////////////////////////////////////////////////////////////////////
/**
* a class of the application.
* used to wrap the websocket object.
*/
exmp.websocket.VisitorSocket = {
    _socket: null,
    init: function() {
        console.log("init");

        // create a new websocket object.
        // if necessary, change it to your server address.
        this._socket = new WebSocket("ws://127.0.0.1:8081/");
        this._socket.onopen = this.open;
        this._socket.onclose = this.close;
        this._socket.onmessage = this.message;
        this._socket.onerror = this.error;
    },
    open: function() {
        console.log("open");
    },
    close: function() {
        console.log("close");
    },
    message: function(message) {
        console.log("message");

        // been pushed a message from WebSocket server.
        var visitor = eval("(" + message.data + ")");
        $("ul.feeds").prepend(
            $("<li />").html(
                "<a class='feed_feeling'>" + visitor.feeling + 
                "</a>" + "<b>" + visitor.person + "</b>" + " from " +
                "<b>" + visitor.country + "</b>"
            )
        )
    },
    send: function(value) {
        console.log("send");

        // a value of string is like that,
        // {"person":"John Lennon","feeling":":)","country":"UK"}
        this._socket.send(value);
    },
    error: function(e) {
        console.log("error");
        console.log(e);
    }
}

///////////////////////////////////////////////////////////////////////////////
/**
* an entry class of the application.
*/
exmp.websocket.Application = function(sender) {
    // the jQuery's way.
    $(document).ready(function() {
        sender.init();
    })
}

///////////////////////////////////////////////////////////////////////////////
/**
* a controller class of the application.
*/
exmp.websocket.Controller = window; {

    ///////////////////////////////////////////////////////////////////////////
    // public methods

    // init the controller class.
    exmp.websocket.Controller.init = function() {
        exmp.websocket.Controller._initComponent();
    }

    ///////////////////////////////////////////////////////////////////////////
    // event handler methods

    // an entry pad window be opened.
    exmp.websocket.Controller._doOpenCommandOnClick = function() {
        $("#open_command").click(function() {
            $("#pad_wrapper").show().css({
                top: -50,
                left: -50
            });
            $("#fine_entry_feeling").click();
        });
    }

    // an entry pad window be closed.
    exmp.websocket.Controller._doCloseCommandOnClick = function() {
        $("#close_command").click(function() {
            $("#pad_wrapper").hide(); 
        });
    }

    // a message be sent to server by the websocket object.
    exmp.websocket.Controller._doSendCommandOnClick = function() {
        $("#send_command").click(function() {
            var first_name = exmp.websocket.Util.escapeHtml($("#first_name_text").val());
            var last_name = exmp.websocket.Util.escapeHtml($("#last_name_text").val());
            var feeling = $("#feel_param_hidden").val();
            var country = exmp.websocket.CountryValueFactory.create();
            var param = '{"person":"' + first_name + " " + last_name + 
                '","feeling":"' + feeling + '","country":"' + country + '"}';

            // send a message using the websocket object.
            exmp.websocket.VisitorSocket.send(
                param
            );
        });
    }

    // a feeling icon is clicked, 
    // the value of the param of feeling is set to hidden area.
    exmp.websocket.Controller._doFineEntryFeelingOnClick = function() {
        $("#fine_entry_feeling").click(function() {
            $("a.entry_feeling").css({"background-color": "silver"});
            $("#fine_entry_feeling").css({"background-color": "lemonchiffon"});
            $("#fine_entry_feeling").focus();
            $("#feel_param_hidden").val(":)");
        });
    }

    exmp.websocket.Controller._doSadEntryFeelingOnClick = function() {
        $("#sad_entry_feeling").click(function() {
            $("a.entry_feeling").css({"background-color": "silver"});
            $("#sad_entry_feeling").css({"background-color": "lemonchiffon"});
            $("#sad_entry_feeling").focus();
            $("#feel_param_hidden").val(":(");
        });
    }

    exmp.websocket.Controller._doCoolEntryFeelingOnClick = function() {
        $("#cool_entry_feeling").click(function() {
            $("a.entry_feeling").css({"background-color": "silver"});
            $("#cool_entry_feeling").css({"background-color": "lemonchiffon"});
            $("#cool_entry_feeling").focus();
            $("#feel_param_hidden").val(":|");
        });
    }

    exmp.websocket.Controller._doWonderEntryFeelingOnClick = function() {
        $("#wonder_entry_feeling").click(function() {
            $("a.entry_feeling").css({"background-color": "silver"});
            $("#wonder_entry_feeling").css({"background-color": "lemonchiffon"});
            $("#wonder_entry_feeling").focus();
            $("#feel_param_hidden").val(":o");
        });
    }

    ///////////////////////////////////////////////////////////////////////////
    // private methods

    // init a component of the controller class.
    exmp.websocket.Controller._initComponent = function() {
        
        // initialize the websocket object.
        exmp.websocket.VisitorSocket.init();
        
        // initialize event handler methods.
        exmp.websocket.Controller._doOpenCommandOnClick();
        exmp.websocket.Controller._doCloseCommandOnClick();
        exmp.websocket.Controller._doSendCommandOnClick();
        exmp.websocket.Controller._doFineEntryFeelingOnClick();
        exmp.websocket.Controller._doSadEntryFeelingOnClick();
        exmp.websocket.Controller._doCoolEntryFeelingOnClick();
        exmp.websocket.Controller._doWonderEntryFeelingOnClick();
        
        // and, do a some initialize.
        $("div.wrapper").draggable();
        $("#pad_wrapper").hide();
    }
}

///////////////////////////////////////////////////////////////////////////////
/**
* a functor class of the application.
* get the value of country
*/
exmp.websocket.CountryValueFactory = {
    // a dummy function..
    create: function() {
        var locale = window.navigator.language.toLowerCase();
        if (locale == "en") {
            return "UK";
        } else if (locale == "ja") {
            return "Japan";
        }
        // defalut is USA..
        return "USA";
    }
}

///////////////////////////////////////////////////////////////////////////////
/**
* an utility class of the application.
*/
exmp.websocket.Util = { 
    escapeHtml: function(value) {
        return $("<div />").text(value).html();
    }
}

///////////////////////////////////////////////////////////////////////////////
/**
* call the main entry.
*/
new exmp.websocket.Application(this);